import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';
import './SeatSelection.css';

const SeatSelectionPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { flightId } = useParams();
    
    const [bookingDetails, setBookingDetails] = useState(null);
    const [passengerCount, setPassengerCount] = useState(1);
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [unavailableSeats, setUnavailableSeats] = useState(new Set());
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [connected, setConnected] = useState(false);
    
    const stompClient = useRef(null);
    const seatPrice = 500;
    const userId = localStorage.getItem('userId') || 'user_' + Math.random().toString(36).substr(2, 9);

    const rows = 10;
    const seatsPerRow = 6;

    useEffect(() => {
        if (location.state) {
            setBookingDetails(location.state.bookingDetails);
            setPassengerCount(location.state.passengerCount || 1);
        } else {
            navigate('/flights');
            return;
        }

        initializeWebSocket();

        return () => {
            if (stompClient.current) {
                // Release all selected seats on unmount
                selectedSeats.forEach(seat => {
                    releaseSeat(seat);
                });
                stompClient.current.deactivate();
                console.log('WebSocket connection closed');
            }
        };
    }, [location.state]);

    const initializeWebSocket = () => {
        if (!bookingDetails?.flightId) {
            console.log('No flight ID available for WebSocket connection');
            setError('No flight information available. Please try again.');
            return;
        }

        const flightIdNum = Number(bookingDetails.flightId);
        if (isNaN(flightIdNum)) {
            setError('Invalid flight ID format');
            return;
        }

        console.log('Initializing WebSocket connection to flight:', flightIdNum);
        
        // Clear previous connection
        if (stompClient.current) {
            stompClient.current.deactivate();
        }

        try {
            stompClient.current = new Client({
                webSocketFactory: () => new SockJS('http://localhost:8080/ws'),
                reconnectDelay: 5000,
                heartbeatIncoming: 4000,
                heartbeatOutgoing: 4000,
                
                debug: function(str) {
                    console.log('STOMP:', str);
                },
                
                onConnect: (frame) => {
                    console.log('✅ WebSocket connected successfully!', frame);
                    setConnected(true);
                    setError('');
                    
                    // Subscribe to seat updates for this flight
                    stompClient.current.subscribe(
                        `/topic/flight/${flightIdNum}/seats`, 
                        (message) => {
                            console.log('Received seat update:', message.body);
                            try {
                                const updatedUnavailableSeats = JSON.parse(message.body);
                                setUnavailableSeats(new Set(updatedUnavailableSeats));
                            } catch (e) {
                                console.error('Error parsing seat data:', e);
                            }
                        }
                    );
                    
                    // Request initial unavailable seats
                    console.log('Requesting initial seat data for flight:', flightIdNum);
                    stompClient.current.publish({
                        destination: `/app/flight/${flightIdNum}/seats`,
                        body: JSON.stringify({}),
                    });
                },
                
                onStompError: (frame) => {
                    console.error('❌ STOMP error:', frame);
                    setError(`WebSocket protocol error: ${frame.headers?.message || 'Unknown error'}`);
                    setConnected(false);
                },
                
                onWebSocketError: (event) => {
                    console.error('❌ WebSocket error:', event);
                    setError(`Cannot connect to server. Please check if the server is running on port 8080.`);
                    setConnected(false);
                },
                
                onDisconnect: (frame) => {
                    console.log('WebSocket disconnected:', frame);
                    setConnected(false);
                }
            });
            
            stompClient.current.activate();
            
        } catch (error) {
            console.error('❌ WebSocket initialization failed:', error);
            setError(`Failed to initialize WebSocket: ${error.message}`);
            setConnected(false);
        }
    };

    const handleSeatSelect = (seatNumber) => {
        if (unavailableSeats.has(seatNumber) || !connected) {
            console.log('Cannot select seat - unavailable or not connected');
            return;
        }

        setSelectedSeats(prev => {
            if (prev.includes(seatNumber)) {
                releaseSeat(seatNumber);
                return prev.filter(seat => seat !== seatNumber);
            } else if (prev.length < passengerCount) {
                reserveSeat(seatNumber);
                return [...prev, seatNumber];
            }
            return prev;
        });
    };

    const reserveSeat = (seatNumber) => {
        if (stompClient.current && connected && bookingDetails) {
            try {
                console.log('Reserving seat:', seatNumber);
                const flightIdNum = Number(bookingDetails.flightId);
                const reservationRequest = {
                    flightId: flightIdNum,
                    seatNumber: seatNumber,
                    userId: userId
                };
                
                stompClient.current.publish({
                    destination: '/app/reserve-seat',
                    body: JSON.stringify(reservationRequest)
                });
            } catch (error) {
                console.error('Error reserving seat:', error);
                setError('Failed to reserve seat. Please try again.');
            }
        }
    };

    const releaseSeat = (seatNumber) => {
        if (stompClient.current && connected && bookingDetails) {
            try {
                console.log('Releasing seat:', seatNumber);
                const flightIdNum = Number(bookingDetails.flightId);
                const reservationRequest = {
                    flightId: flightIdNum,
                    seatNumber: seatNumber,
                    userId: userId
                };
                
                stompClient.current.publish({
                    destination: '/app/release-seat',
                    body: JSON.stringify(reservationRequest)
                });
            } catch (error) {
                console.error('Error releasing seat:', error);
            }
        }
    };

    const handleConfirmSeats = async () => {
        if (selectedSeats.length !== passengerCount) {
            setError(`Please select exactly ${passengerCount} seat(s)`);
            return;
        }

        try {
            setLoading(true);
            
            navigate(`/flights/booking/${bookingDetails.id}`, {
                state: {
                    bookingDetails: {
                        ...bookingDetails,
                        selectedSeats: selectedSeats,
                        seatCharges: selectedSeats.length * seatPrice,
                        totalAmount: (bookingDetails.totalAmount || 0) + (selectedSeats.length * seatPrice)
                    },
                    selectedSeats,
                    seatCharges: selectedSeats.length * seatPrice,
                    totalAmount: (bookingDetails.totalAmount || 0) + (selectedSeats.length * seatPrice)
                }
            });
            
        } catch (err) {
            setError('Failed to proceed to payment');
            console.error('Navigation error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleManualSeatSelection = (seatNumber) => {
        // Fallback function if WebSocket is not connected
        if (unavailableSeats.has(seatNumber)) {
            return;
        }

        setSelectedSeats(prev => {
            if (prev.includes(seatNumber)) {
                return prev.filter(seat => seat !== seatNumber);
            } else if (prev.length < passengerCount) {
                return [...prev, seatNumber];
            }
            return prev;
        });
    };

    const renderSeatMap = () => {
        const seatMap = [];
        
        // Add cockpit area
        seatMap.push(
            <div key="cockpit" className="cockpit-area">
                <div className="cockpit">Cockpit</div>
            </div>
        );
        
        // Add seat rows
        for (let row = 1; row <= rows; row++) {
            const rowSeats = [];
            
            // Left side seats (A, B, C)
            for (let seat = 1; seat <= 3; seat++) {
                const seatNumber = (row - 1) * seatsPerRow + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isUnavailable = unavailableSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                const isEmergencyExit = row === 1 || row === rows;
                
                rowSeats.push(
                    <div
                        key={`left-${seatNumber}`}
                        className={`seat ${isUnavailable ? 'booked' : ''} ${isSelected ? 'selected' : ''} ${isEmergencyExit ? 'emergency' : ''}`}
                        onClick={() => !isUnavailable && handleManualSeatSelection(seatNumber)}
                        title={isUnavailable ? 'Already booked' : `Seat ${seatLabel}`}
                    >
                        {seatLabel}
                    </div>
                );
            }

            // Aisle
            rowSeats.push(<div key={`aisle-${row}`} className="aisle"></div>);

            // Right side seats (D, E, F)
            for (let seat = 4; seat <= 6; seat++) {
                const seatNumber = (row - 1) * seatsPerRow + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isUnavailable = unavailableSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                const isEmergencyExit = row === 1 || row === rows;
                
                rowSeats.push(
                    <div
                        key={`right-${seatNumber}`}
                        className={`seat ${isUnavailable ? 'booked' : ''} ${isSelected ? 'selected' : ''} ${isEmergencyExit ? 'emergency' : ''}`}
                        onClick={() => !isUnavailable && handleManualSeatSelection(seatNumber)}
                        title={isUnavailable ? 'Already booked' : `Seat ${seatLabel}`}
                    >
                        {seatLabel}
                    </div>
                );
            }

            seatMap.push(
                <div key={`row-${row}`} className="seat-row">
                    {rowSeats}
                </div>
            );
        }

        return seatMap;
    };

    const isConfirmEnabled = selectedSeats.length === passengerCount && !loading;

    if (!bookingDetails) {
        return (
            <div className="error-container">
                <h3>No Booking Information</h3>
                <p>No booking information found. Please start over.</p>
                <button onClick={() => navigate('/flights')} className="retry-button">
                    Back to Flights
                </button>
            </div>
        );
    }

    return (
        <div className="seat-selection-page">
            <div className="container">
                <div className="header">
                    <h1>Select Your Seats</h1>
                    {bookingDetails && (
                        <p>Flight {bookingDetails.flightNumber} - {bookingDetails.departureFrom} to {bookingDetails.arrivalTo}</p>
                    )}
                    <div className={`connection-status ${connected ? 'connected' : 'disconnected'}`}>
                        {connected ? '✅ Connected' : '❌ Disconnected'}
                    </div>
                </div>

                {error && (
                    <div className="error-message">
                        <strong>Connection Status:</strong> {error}
                        <button 
                            onClick={() => {
                                setError('');
                                setConnectionAttempts(0);
                                initializeWebSocket();
                            }} 
                            className="retry-button-small"
                        >
                            Retry Connection
                        </button>
                    </div>
                )}

                {bookingDetails && (
                    <div className="booking-summary">
                        <h3>Booking Summary</h3>
                        <div className="summary-details">
                            <p><strong>Booking Reference:</strong> {bookingDetails.bookingReference}</p>
                            <p><strong>Passengers:</strong> {passengerCount}</p>
                            <p><strong>Flight Date:</strong> {bookingDetails.departureDate || 'N/A'}</p>
                            <p><strong>Departure Time:</strong> {bookingDetails.departureTime || 'N/A'}</p>
                        </div>
                    </div>
                )}

                <div className="debug-info">
                    <h4>Debug Information</h4>
                    <p>Flight ID: {bookingDetails?.flightId || 'N/A'}</p>
                    <p>WebSocket Status: {connected ? 'Connected' : 'Disconnected'}</p>
                    <p>Connection Attempts: {connectionAttempts}</p>
                    <p>Unavailable Seats: {Array.from(unavailableSeats).join(', ') || 'None'}</p>
                    <p>Server URL: http://localhost:8080/ws</p>
                </div>

                <div className="connection-troubleshooting">
                    <h4>⚠️ Connection Troubleshooting</h4>
                    <p>If WebSocket connection fails:</p>
                    <ol>
                        <li>Ensure your Spring Boot server is running on port 8080</li>
                        <li>Check that WebSocket is enabled in your backend</li>
                        <li>Verify CORS settings in your Spring Boot configuration</li>
                        <li>Try accessing <a href="http://localhost:8080/ws" target="_blank" rel="noopener noreferrer">http://localhost:8080/ws</a> in your browser</li>
                    </ol>
                </div>

                <div className="seat-selection-container">
                    <div className="airplane-layout">
                        <div className="airplane">
                            {renderSeatMap()}
                        </div>
                    </div>

                    <div className="selection-info">
                        <div className="seat-legend">
                            <h4>Seat Legend</h4>
                            <div className="legend-item">
                                <div className="seat available"></div>
                                <span>Available</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat selected"></div>
                                <span>Selected</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat booked"></div>
                                <span>Unavailable</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat emergency"></div>
                                <span>Emergency Exit</span>
                            </div>
                        </div>

                        <div className="selected-seats-info">
                            <h4>Your Selection</h4>
                            {selectedSeats.length > 0 ? (
                                <div>
                                    <p>Selected Seats: {selectedSeats.map(seat => {
                                        const row = Math.ceil(seat / seatsPerRow);
                                        const seatLetter = String.fromCharCode(64 + (seat % seatsPerRow === 0 ? seatsPerRow : seat % seatsPerRow));
                                        return `${row}${seatLetter}`;
                                    }).join(', ')}</p>
                                    <p>Seat Charges: ₹{selectedSeats.length * seatPrice}</p>
                                    {bookingDetails && (
                                        <p><strong>Total Amount: ₹{(bookingDetails.totalAmount || 0) + (selectedSeats.length * seatPrice)}</strong></p>
                                    )}
                                </div>
                            ) : (
                                <p>No seats selected yet</p>
                            )}
                        </div>

                        <div className="action-buttons">
                            <button 
                                className="btn-back"
                                onClick={() => navigate(-1)}
                                disabled={loading}
                            >
                                Back to Booking
                            </button>
                            <button 
                                className={`btn-confirm ${isConfirmEnabled ? '' : 'disabled'}`}
                                onClick={isConfirmEnabled ? handleConfirmSeats : undefined}
                                disabled={!isConfirmEnabled}
                            >
                                {loading ? 'Processing...' : `Confirm ${selectedSeats.length}/${passengerCount} Seats`}
                            </button>
                        </div>

                        {!connected && (
                            <div className="connection-warning">
                                <p>⚠️ WebSocket not connected. Seat availability may not be accurate.</p>
                                <p>You can still select seats and proceed to payment.</p>
                            </div>
                        )}

                        {selectedSeats.length !== passengerCount && (
                            <div className="selection-warning">
                                <p>Please select {passengerCount} seat(s) to continue.</p>
                            </div>
                        )}
                    </div>
                </div>

                <div className="seat-info-note">
                    <p><strong>Note:</strong> Seat selection charges are ₹{seatPrice} per seat. Emergency exit rows have extra legroom.</p>
                    <p>Selected seats are temporarily reserved for you while you complete your booking.</p>
                </div>
            </div>
        </div>
    );
};

export default SeatSelectionPage;
