import { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import { 
  AssistsAPI, 
  DeleteFlightById, 
  retrieveAFlightById, 
  retrieveAllFlights, 
  retrieveFlightFromTo 
} from "../api/CallingApi";

function FlightList() {
  // State management
  const [state, setState] = useState({
    flights: [],
    message: null,
    searchParams: {
      departure: "",
      arrival: "",
      id: ""
    },
    aiQuery: "",
    loading: {
      main: false,
      search: false,
      ai: false
    }
  });

  // Chatbot state
  const [chatMessages, setChatMessages] = useState([
    { text: "Hello! How can I help you find flights today?", sender: 'bot' }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [showChat, setShowChat] = useState(false);

  const navigate = useNavigate();

  // Derived values
  const { flights, message, searchParams, aiQuery, loading } = state;

  // Fetch all flights on component mount
  useEffect(() => {
    fetchAllFlights();
  }, []);

  // Helper function to update state
  const updateState = (updates) => {
    setState(prev => ({ ...prev, ...updates }));
  };

  // Fetch all flights
  const fetchAllFlights = async () => {
    updateState({ 
      loading: { ...loading, main: true },
      message: "Loading flights..."
    });

    try {
      const response = await retrieveAllFlights();
      updateState({
        flights: response.data || [],
        message: null,
        loading: { ...loading, main: false }
      });
    } catch (error) {
      console.error("Failed to fetch flights:", error);
      updateState({
        message: "Failed to load flights. Please try again.",
        loading: { ...loading, main: false }
      });
    }
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    updateState({
      searchParams: {
        ...searchParams,
        [name]: value
      }
    });
  };

  // Search by flight ID
  const searchById = async () => {
    if (!searchParams.id.trim()) {
      updateState({ message: "Please enter a flight ID" });
      return;
    }

    updateState({ 
      loading: { ...loading, search: true },
      message: "Searching for flight..."
    });

    try {
      const response = await retrieveAFlightById(searchParams.id);
      if (response.data) {
        updateState({
          flights: [response.data],
          message: null,
          loading: { ...loading, search: false }
        });
      } else {
        updateState({
          flights: [],
          message: "No flight found with this ID",
          loading: { ...loading, search: false }
        });
      }
    } catch (error) {
      console.error("Search error:", error);
      updateState({
        message: "Error searching for flight",
        flights: [],
        loading: { ...loading, search: false }
      });
    }
  };

  // Search by route
  const searchByRoute = async (from, to) => {
    const departure = (from || searchParams.departure).trim();
    const arrival = (to || searchParams.arrival).trim();

    if (!departure && !arrival) {
      updateState({ message: "Please enter at least one location" });
      return;
    }

    updateState({
      loading: { ...loading, search: true },
      message: "Searching flights...",
      flights: []
    });

    try {
      const response = await retrieveFlightFromTo(
        departure || "any", 
        arrival || "any"
      );

      if (!response?.data) {
        throw new Error("Invalid response format");
      }

      updateState({
        flights: response.data,
        message: response.data.length ? null : 
          `No flights found from ${departure || 'anywhere'} to ${arrival || 'anywhere'}`,
        loading: { ...loading, search: false }
      });
    } catch (error) {
      console.error("Search failed:", error);
      
      let errorMessage = "Failed to search flights";
      if (error.response) {
        errorMessage = error.response.data?.message || 
                     `Server error: ${error.response.status}`;
      } else if (error.request) {
        errorMessage = "Network error - please check your connection";
      }

      updateState({
        message: errorMessage,
        flights: [],
        loading: { ...loading, search: false }
      });
    }
  };

  // Delete a flight
  const deleteFlight = async (id) => {
    if (!window.confirm(`Are you sure you want to delete flight ${id}?`)) return;

    try {
      await DeleteFlightById(id);
      updateState({
        message: `Flight ${id} deleted successfully`
      });
      await fetchAllFlights(); // Refresh the list
    } catch (error) {
      console.error("Delete error:", error);
      updateState({
        message: "Failed to delete flight"
      });
    }
  };

  // AI search
  const handleAISearch = async () => {
    if (!aiQuery.trim()) {
      updateState({ message: "Please enter a search query" });
      return;
    }

    updateState({
      loading: { ...loading, ai: true },
      message: "Processing your request...",
      flights: []
    });

    try {
      const response = await AssistsAPI(aiQuery);
      
      if (!response?.data) {
        throw new Error("No response from server");
      }

      const { departureFrom, arrivalTo } = response.data;
      
      // Update search fields if locations were found
      if (departureFrom || arrivalTo) {
        updateState({
          searchParams: {
            ...searchParams,
            departure: departureFrom || searchParams.departure,
            arrival: arrivalTo || searchParams.arrival
          }
        });
        
        // Perform the search
        await searchByRoute(departureFrom || "any", arrivalTo || "any");
      } else {
        updateState({
          message: "Could not determine locations from your query"
        });
      }
    } catch (error) {
      console.error("AI search error:", error);
      updateState({
        message: error.message || "Failed to process your request"
      });
    } finally {
      updateState({
        loading: { ...loading, ai: false }
      });
    }
  };

  // Chatbot functions
  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;

    // Add user message
    const userMessage = { text: chatInput, sender: 'user' };
    setChatMessages(prev => [...prev, userMessage]);
    setChatInput('');
    
    try {
      // Get AI response
      const response = await AssistsAPI(chatInput);
      const { departureFrom, arrivalTo } = response.data;
      
      // Add bot response
      setChatMessages(prev => [
        ...prev,
        { 
          text: departureFrom === 'any' 
            ? `Showing flights to ${arrivalTo}` 
            : arrivalTo === 'any'
            ? `Showing flights from ${departureFrom}`
            : `Showing flights from ${departureFrom} to ${arrivalTo}`,
          sender: 'bot' 
        }
      ]);

      // Trigger the search
      await searchByRoute(departureFrom, arrivalTo);

    } catch (error) {
      setChatMessages(prev => [
        ...prev,
        { text: "Sorry, I didn't understand that. Try something like 'flights to Paris' or 'from New York to London'", sender: 'bot' }
      ]);
    }
  };

  // Navigation functions
  const navigateToFlight = (id) => navigate(`/AddNewFlight/${id}`);
  const addNewFlight = () => navigate('/AddNewFlight/-1');

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Flight Management</h1>
      
      {/* Status Message */}
      {message && (
        <div className={`alert ${
          message.includes("success") ? "alert-success" : 
          message.includes("error") ? "alert-danger" : "alert-info"
        }`}>
          {message}
        </div>
      )}

      {/* Search Controls */}
      <div className="card mb-4">
        <div className="card-body">
          <div className="row g-3 mb-3">
            <div className="col-md-5">
              <input
                type="text"
                name="departure"
                placeholder="Departure City"
                className="form-control"
                value={searchParams.departure}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-5">
              <input
                type="text"
                name="arrival"
                placeholder="Arrival City"
                className="form-control"
                value={searchParams.arrival}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-2">
              <button
                className="btn btn-primary w-100"
                onClick={() => searchByRoute()}
                disabled={loading.search}
              >
                {loading.search ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Searching...
                  </>
                ) : "Search Flights"}
              </button>
            </div>
          </div>

          <div className="row g-3 align-items-center mb-3">
            <div className="col-md-4">
              <div className="input-group">
                <input
                  type="search"
                  name="id"
                  placeholder="Flight ID"
                  className="form-control"
                  value={searchParams.id}
                  onChange={handleInputChange}
                />
                <button
                  className="btn btn-outline-secondary"
                  onClick={searchById}
                  disabled={loading.search}
                >
                  Search
                </button>
              </div>
            </div>
            <div className="col-md-2">
              <button
                className="btn btn-success w-100"
                onClick={fetchAllFlights}
                disabled={loading.main}
              >
                Show All
              </button>
            </div>
          </div>

          {/* AI Search */}
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Try: 'flights to Paris' or 'from New York'"
              value={aiQuery}
              onChange={(e) => updateState({ aiQuery: e.target.value })}
              onKeyPress={(e) => e.key === 'Enter' && handleAISearch()}
            />
            <button
              className="btn btn-info"
              onClick={handleAISearch}
              disabled={loading.ai}
            >
              {loading.ai ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Processing
                </>
              ) : 'AI Search'}
            </button>
          </div>
        </div>
      </div>

      {/* Flight Table */}
      <div className="table-responsive">
        <table className="table table-striped table-hover">
          <thead className="table-dark">
            <tr>
              <th>ID</th>
              <th>Departure</th>
              <th>Arrival</th>
              <th>Departure Time</th>
              <th>Return Time</th>
              <th>Seats</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {loading.main ? (
              <tr>
                <td colSpan="7" className="text-center">
                  <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </div>
                </td>
              </tr>
            ) : flights.length > 0 ? (
              flights.map(flight => (
                <tr key={flight.id}>
                  <td>{flight.id}</td>
                  <td>{flight.departureFrom}</td>
                  <td>{flight.arrivalTo}</td>
                  <td>{new Date(flight.departure).toLocaleString()}</td>
                  <td>{flight.returnDate ? new Date(flight.returnDate).toLocaleString() : 'N/A'}</td>
                  <td>{flight.seatsAvailable}</td>
                  <td>
                    <div className="btn-group">
                      <button
                        className="btn btn-sm btn-warning me-2"
                        onClick={() => navigateToFlight(flight.id)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => deleteFlight(flight.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center">
                  No flights found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Add New Flight Button */}
      <button
        className="btn btn-success mt-3"
        onClick={addNewFlight}
      >
        Add New Flight
      </button>

      {/* Chatbot Toggle Button */}
      <button 
        className="btn btn-primary position-fixed"
        style={{ bottom: '20px', right: '20px', borderRadius: '50%', width: '60px', height: '60px' }}
        onClick={() => setShowChat(!showChat)}
      >
        <i className="bi bi-chat-dots fs-4"></i>
      </button>

      {/* Chatbot Container */}
      {showChat && (
        <div className="position-fixed" style={{ bottom: '90px', right: '20px', width: '350px' }}>
          <div className="card shadow-lg">
            <div className="card-header d-flex justify-content-between align-items-center bg-primary text-white">
              <h5 className="mb-0">Flight Assistant</h5>
              <button 
                className="btn btn-sm btn-light"
                onClick={() => setShowChat(false)}
              >
                <i className="bi bi-x"></i>
              </button>
            </div>
            <div className="card-body p-0" style={{ height: '300px', overflowY: 'auto' }}>
              <div className="p-3">
                {chatMessages.map((msg, i) => (
                  <div 
                    key={i} 
                    className={`mb-2 d-flex ${msg.sender === 'user' ? 'justify-content-end' : 'justify-content-start'}`}
                  >
                    <div 
                      className={`p-2 rounded ${msg.sender === 'user' ? 'bg-primary text-white' : 'bg-light'}`}
                      style={{ maxWidth: '80%' }}
                    >
                      {msg.text}
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="card-footer">
              <form onSubmit={handleChatSubmit} className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Type your flight request..."
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                />
                <button type="submit" className="btn btn-primary ms-2">
                  <i className="bi bi-send"></i>
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default FlightList;
