import { useEffect, useState,} from "react";

import { retrieveAFlightById, retrieveAllFlights } from "../api/CallingApi";
// import { useNavigate } from "react-router-dom";
// import { useAuth } from "./security/AuthContext";

function FlightList(){ 

        // const authContext= useAuth()

        const [flights,setFlights] = useState([])

        const [message,setMessage] = useState(null)

        const[searchId,setsearchId] = useState("")

        // const nevigate=useNavigate();

        useEffect(() => {
           flightList();
     }, []);

        function flightList(){

                retrieveAllFlights()
                .then( (response) =>
                    {
                        // console.log(response.data)
                        setFlights(response.data)
                    } 
                )
                .catch( error =>console.log(error))
           
        }

        //ById
        // Search by ID function
    function searchById() {
        if (!searchId.trim()) {
            setMessage("Please enter a flight ID");
            return;
        }
        retrieveAFlightById(searchId)
            .then((response) => {
                if (response.data) {
                    setFlights([response.data]); // Wrap in array for consistent mapping
                    setMessage(null);
                } else {
                    setFlights([]);
                    setMessage("No flight found with that ID");
                }
            })
            .catch(error => {
                console.error(error);
                setMessage("Error searching for flight");
                setFlights([]);
            })
    }

        // function searchBar(username){

        //     // console.log('Search here ' + username)

        //     retrieveAStudent(username)
        //     .then( (response) =>
        //         {
        //             // console.log(response)
        //             setTodos(response.data)
        //         } 
        //         )
        //     .catch( error =>console.log(error))
        // }

        function HandleSearch(event){
                setsearchId(event.target.value)
            }

        // function deleteStudent(id){
        //     //  console.log('delete operation' + id);

        //     DeleteStudentById(id)
        //     .then( 

        //         () => 
        //             {
        //             //display message
        //             setmessage(`The todo with id ${id} is deleted`)
        //             //update todolist
        //             refreshTodos()

        //         }
        //         )
        //     .catch( error =>console.log(error))
        // }

//    function UpdateTodo(id){
//     //  console.log('Update operation' + id);
     
//      nevigate(`/CreateNewStudent/${id}`)

//    }

//    function CreateNewStudent(){

//     // console.log('Create operation');
 
//     nevigate(`/CreateNewStudent/-1`)
//    }


    return(
        <div className="container">

           <h1>Flights Lists</h1>
           {message && <div className="alert alert-warning"> {message} </div> }

           <input type="search" placeholder="search student name"  onChange={HandleSearch} />
           <button onClick= {searchById(searchId)} >Search</button>
           
           <table className='table'>

                <thead>
                    <tr>
                        <td>id</td>
                        <td>departureFrom</td>
                        <td>arrivalTo</td>
                        <td>departure</td>
                        <td>returnDate</td>
                        <td>seatsAvailable</td>
                    </tr>
                </thead>

                <tbody >
                    {
                        flights.map(
                            flight => (

                                <tr key={flight.id}>

                                    <td>{flight.id}</td>
                                    <td>{flight.departureFrom}</td>
                                    <td>{flight.arrivalTo}</td>
                                    <td>{flight.departure}</td>
                                    <td>{flight.returnDate}</td>
                                    <td>{flight.seatsAvailable}</td>
                                     {/* <td><button className="alert alert-warning"  onClick={ ()=> deleteStudent(todo.id)}>Delete</button> </td>
                                    <td><button className="alert alert-success"  onClick={ ()=> UpdateTodo(todo.id)}>Update</button> </td>  */}
                               </tr>
                            )
                        )
                        
                    }
                        {/* <button className="btn btn-success m-5" onClick={CreateNewStudent}> Add new Student</button> */}
                     </tbody>
               

           </table>

        </div>
    )
}

export default FlightList
