import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { ConfirmBookingAPI } from "../api/CallingApi";
import "./PaymentPage.css"; // Make sure to create this CSS file

export default function PaymentPage() {
    const navigate = useNavigate();
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState("credit");
    const [formData, setFormData] = useState({
        cardNumber: "",
        cardName: "",
        expiry: "",
        cvv: "",
        upiId: "",
        bank: ""
    });
    const [formErrors, setFormErrors] = useState({});

    // Get booking details from navigation state
    const { bookingDetails, passengerCount } = location.state || {};
    const [paymentAmount] = useState(passengerCount * 5000); // Example: 5000 per passenger

    useEffect(() => {
        if (!bookingDetails) {
            navigate("/"); // Redirect if no booking details
        }
    }, [bookingDetails, navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const validateForm = () => {
        const errors = {};
        
        if (paymentMethod === "credit" || paymentMethod === "debit") {
            if (!formData.cardNumber || !/^\d{16}$/.test(formData.cardNumber)) {
                errors.cardNumber = "Please enter a valid 16-digit card number";
            }
            if (!formData.cardName.trim()) {
                errors.cardName = "Card holder name is required";
            }
            if (!formData.expiry || !/^\d{2}\/\d{2}$/.test(formData.expiry)) {
                errors.expiry = "Please enter expiry in MM/YY format";
            }
            if (!formData.cvv || !/^\d{3,4}$/.test(formData.cvv)) {
                errors.cvv = "Please enter a valid CVV";
            }
        } else if (paymentMethod === "upi") {
            if (!formData.upiId || !/[\w.-]+@[\w]+/.test(formData.upiId)) {
                errors.upiId = "Please enter a valid UPI ID";
            }
        } else if (paymentMethod === "netbanking") {
            if (!formData.bank) {
                errors.bank = "Please select a bank";
            }
        }
        
        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handlePayment = async () => {
        if (!validateForm()) return;

        try {
            setLoading(true);
            setError(null);
            
            const paymentData = {
                paymentAmount: paymentAmount
            };

            const response = await ConfirmBookingAPI(bookingDetails.id, paymentData);
            
            // Redirect to success page after successful payment
            navigate("/booking-success", {
                state: {
                    bookingId: bookingDetails.id,
                    paymentId: response.data.id,
                    amount: paymentAmount
                }
            });
            
        } catch (error) {
            console.error("Payment failed:", error);
            setError(error.response?.data?.message || "Payment failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    if (!bookingDetails) {
        return (
            <div className="container">
                <div className="error-message">No booking details found. Please start over.</div>
                <button onClick={() => navigate("/")} className="back-button">
                    Return to Home
                </button>
            </div>
        );
    }

    return (
        <div className="payment-container">
            <h1>Complete Your Payment</h1>
            
            <div className="payment-grid">
                <div className="booking-summary">
                    <h2>Booking Summary</h2>
                    <div className="summary-card">
                        <div className="summary-item">
                            <span>Flight:</span>
                            <span>{bookingDetails.flight.departureFrom} → {bookingDetails.flight.arrivalTo}</span>
                        </div>
                        <div className="summary-item">
                            <span>Date:</span>
                            <span>{new Date(bookingDetails.flight.departureDateTime).toLocaleDateString()}</span>
                        </div>
                        <div className="summary-item">
                            <span>Passengers:</span>
                            <span>{passengerCount}</span>
                        </div>
                        <div className="summary-item total">
                            <span>Total Amount:</span>
                            <span>₹{paymentAmount.toLocaleString()}</span>
                        </div>
                    </div>
                </div>
                
                <div className="payment-section">
                    <h2>Payment Method</h2>
                    <div className="payment-methods">
                        <div className="method-options">
                            <label className={`method-option ${paymentMethod === "credit" ? "active" : ""}`}>
                                <input
                                    type="radio"
                                    name="paymentMethod"
                                    value="credit"
                                    checked={paymentMethod === "credit"}
                                    onChange={() => setPaymentMethod("credit")}
                                />
                                Credit Card
                            </label>
                            
                            <label className={`method-option ${paymentMethod === "debit" ? "active" : ""}`}>
                                <input
                                    type="radio"
                                    name="paymentMethod"
                                    value="debit"
                                    checked={paymentMethod === "debit"}
                                    onChange={() => setPaymentMethod("debit")}
                                />
                                Debit Card
                            </label>
                            
                            <label className={`method-option ${paymentMethod === "upi" ? "active" : ""}`}>
                                <input
                                    type="radio"
                                    name="paymentMethod"
                                    value="upi"
                                    checked={paymentMethod === "upi"}
                                    onChange={() => setPaymentMethod("upi")}
                                />
                                UPI
                            </label>
                            
                            <label className={`method-option ${paymentMethod === "netbanking" ? "active" : ""}`}>
                                <input
                                    type="radio"
                                    name="paymentMethod"
                                    value="netbanking"
                                    checked={paymentMethod === "netbanking"}
                                    onChange={() => setPaymentMethod("netbanking")}
                                />
                                Net Banking
                            </label>
                        </div>
                        
                        <div className="payment-details">
                            {paymentMethod === "credit" || paymentMethod === "debit" ? (
                                <div className="card-form">
                                    <div className="form-group">
                                        <label>Card Number</label>
                                        <input
                                            type="text"
                                            name="cardNumber"
                                            value={formData.cardNumber}
                                            onChange={handleInputChange}
                                            placeholder="1234 5678 9012 3456"
                                            maxLength="16"
                                        />
                                        {formErrors.cardNumber && <span className="error">{formErrors.cardNumber}</span>}
                                    </div>
                                    
                                    <div className="form-group">
                                        <label>Card Holder Name</label>
                                        <input
                                            type="text"
                                            name="cardName"
                                            value={formData.cardName}
                                            onChange={handleInputChange}
                                            placeholder="John Doe"
                                        />
                                        {formErrors.cardName && <span className="error">{formErrors.cardName}</span>}
                                    </div>
                                    
                                    <div className="form-row">
                                        <div className="form-group">
                                            <label>Expiry Date</label>
                                            <input
                                                type="text"
                                                name="expiry"
                                                value={formData.expiry}
                                                onChange={handleInputChange}
                                                placeholder="MM/YY"
                                                maxLength="5"
                                            />
                                            {formErrors.expiry && <span className="error">{formErrors.expiry}</span>}
                                        </div>
                                        
                                        <div className="form-group">
                                            <label>CVV</label>
                                            <input
                                                type="password"
                                                name="cvv"
                                                value={formData.cvv}
                                                onChange={handleInputChange}
                                                placeholder="123"
                                                maxLength="4"
                                            />
                                            {formErrors.cvv && <span className="error">{formErrors.cvv}</span>}
                                        </div>
                                    </div>
                                </div>
                            ) : paymentMethod === "upi" ? (
                                <div className="upi-form">
                                    <div className="form-group">
                                        <label>UPI ID</label>
                                        <input
                                            type="text"
                                            name="upiId"
                                            value={formData.upiId}
                                            onChange={handleInputChange}
                                            placeholder="yourname@upi"
                                        />
                                        {formErrors.upiId && <span className="error">{formErrors.upiId}</span>}
                                    </div>
                                    <p className="upi-note">e.g. name@bankname or 1234567890@upi</p>
                                </div>
                            ) : (
                                <div className="netbanking-form">
                                    <div className="form-group">
                                        <label>Select Bank</label>
                                        <select
                                            name="bank"
                                            value={formData.bank}
                                            onChange={handleInputChange}
                                        >
                                            <option value="">-- Select Bank --</option>
                                            <option value="sbi">State Bank of India</option>
                                            <option value="hdfc">HDFC Bank</option>
                                            <option value="icici">ICICI Bank</option>
                                            <option value="axis">Axis Bank</option>
                                            <option value="kotak">Kotak Mahindra Bank</option>
                                        </select>
                                        {formErrors.bank && <span className="error">{formErrors.bank}</span>}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                    
                    {error && <div className="error-message">{error}</div>}
                    
                    <div className="payment-actions">
                        <button 
                            className="pay-now-button"
                            onClick={handlePayment}
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="spinner"></span> Processing...
                                </>
                            ) : (
                                `Pay ₹${paymentAmount.toLocaleString()}`
                            )}
                        </button>
                        
                        <button 
                            className="cancel-button"
                            onClick={() => navigate(-1)}
                            disabled={loading}
                        >
                            Cancel
                        </button>
                    </div>
                    
                    <div className="security-note">
                        <span className="lock-icon">🔒</span>
                        <span>Your payment is secure and encrypted</span>
                    </div>
                </div>
            </div>
        </div>
    );
}
