// import { DeleteStudentById, retrieveAllStudents, retrieveAStudent } from "./api/HelloWorldCallAPI";
import { useEffect, useState,/*refreshTodos*/ } from "react";
import { retrieveAllFlights } from "../api/CallingApi";
// import { useNavigate } from "react-router-dom";
// import { useAuth } from "./security/AuthContext";

function FlightList(){ 

        // const authContext= useAuth()

        const [flights,setFlights] = useState([])

        const [error,seterror] = useState(null)
        const [loading,setloading] = useState(null)

        // const[username,setUsername] = useState([])

        // const nevigate=useNavigate();

        useEffect( () => flightList() )

        function flightList(){

                retrieveAllFlights()
                .then( (response) =>
                    {
                        // console.log(response.data)
                        setFlights(response.data)
                    } 
                )
                .catch( error =>console.log(error))
           
        }

        // function searchBar(username){

        //     // console.log('Search here ' + username)

        //     retrieveAStudent(username)
        //     .then( (response) =>
        //         {
        //             // console.log(response)
        //             setTodos(response.data)
        //         } 
        //         )
        //     .catch( error =>console.log(error))
        // }

        // function HandleSearch(event){
        //         setUsername(event.target.value)
        //     }

        // function deleteStudent(id){
        //     //  console.log('delete operation' + id);

        //     DeleteStudentById(id)
        //     .then( 

        //         () => 
        //             {
        //             //display message
        //             setmessage(`The todo with id ${id} is deleted`)
        //             //update todolist
        //             refreshTodos()

        //         }
        //         )
        //     .catch( error =>console.log(error))
        // }

//    function UpdateTodo(id){
//     //  console.log('Update operation' + id);
     
//      nevigate(`/CreateNewStudent/${id}`)

//    }

//    function CreateNewStudent(){

//     // console.log('Create operation');
 
//     nevigate(`/CreateNewStudent/-1`)
//    }

 const formatDate = (dateString) => {
        if (!dateString) return "-";
        const date = new Date(dateString);
        return date.toLocaleDateString();
    };

    return (
        <div className="container">
            <h1 className="my-4">Flights List</h1>
            
            {loading && <div className="alert alert-info">Loading flights...</div>}
            {error && <div className="alert alert-danger">{error}</div>}
            
            <div className="table-responsive">
                <table className="table table-striped table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>ID</th>
                            <th>Departure From</th>
                            <th>Arrival To</th>
                            <th>Departure Date</th>
                            <th>Return Date</th>
                            <th>Seats Available</th>
                        </tr>
                    </thead>
                    <tbody>
                        {flights.length > 0 ? (
                            flights.map(flight => (
                                <tr key={flight.id}>
                                    <td>{flight.id}</td>
                                    <td>{flight.departureFrom || "-"}</td>
                                    <td>{flight.arrivalTo || "-"}</td>
                                    <td>{formatDate(flight.departure)}</td>
                                    <td>{formatDate(flight.returnDate)}</td>
                                    <td>{flight.seatsAvailable || "0"}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6" className="text-center">
                                    {!loading && "No flights available"}
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default FlightList
