import { ErrorMessage, Field, Formik } from "formik";
import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { BookedFlightAPI, retrieveAFlightById } from "../api/CallingApi";
import "./BookedFlight.css";

export default function BookedFlight() {
    const navigate = useNavigate();
    const { id } = useParams();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [initialValues, setInitialValues] = useState({
        flightId: id !== "-1" ? id : "",
        departureFrom: "",
        arrivalTo: "",
        departureDate: "",
        departureTime: "",
        returnDate: "",
        seatsAvailable: "",
        passenger: "",
        passengerName: "",
        passengerName2: "",
        passengerAdult: "",
        passengerAdult2: "",
        dateOfBirth: "",
        dateOfBirth2: "",
        contactNo: ""
    });

    useEffect(() => {
        const fetchFlightById = async () => {
            if (id && id !== "-1") {
                try {
                    setLoading(true);
                    setError(null);
                    
                    const response = await retrieveAFlightById(id);
                    console.log("API Response:", response);
                    
                    if (!response.data) {
                        throw new Error("No data received from server");
                    }

                    setInitialValues({
                        ...initialValues,
                        flightId: id,
                        departureFrom: response.data.departureFrom || "",
                        arrivalTo: response.data.arrivalTo || "",
                        departureDate: response.data.departureDate || response.data.departure || "",
                        departureTime: response.data.departureTime || "",
                        returnDate: response.data.returnDate || "",
                        seatsAvailable: response.data.seatsAvailable || response.data.availableSeats || ""
                    });
                    
                } catch (error) {
                    console.error("Error fetching flight:", error);
                    setError(error.message || "Failed to load flight data");
                } finally {
                    setLoading(false);
                }
            } else {
                setLoading(false);
            }
        };
        
        fetchFlightById();
    }, [id]);

    function onSubmit(values) {
        const ticket = {
            flightId: values.flightId,
            departureFrom: values.departureFrom,
            arrivalTo: values.arrivalTo,
            departure: values.departure,
            departureTime: values.departureTime,
            returnDate: values.returnDate,
            seatsAvailable: values.seatsAvailable,
            passenger: values.passenger,
            passengerName: values.passengerName,
            passengerName2: values.passengerName2,
            passengerAdult: values.passengerAdult,
            passengerAdult2: values.passengerAdult2,
            dateOfBirth: values.dateOfBirth,
            dateOfBirth2: values.dateOfBirth2,
            contactNo: values.contactNo
        };

        BookedFlightAPI(ticket)
            .then(response => {
                navigate("/successpage");
            })
            .catch(error => {
                console.error("Booking failed:", error);
                alert("Booking failed. Please try again.");
            });
    }

    function validate(values) {
        let errors = {};
        if (!values.passengerName) {
            errors.passengerName = "Passenger name is required";
        }
        if (!values.flightId && id !== "-1") {
            errors.flightId = "Flight ID is required";
        }
        if (!values.contactNo) {
            errors.contactNo = "Contact number is required";
        }
        return errors;
    }

    if (loading) {
        return <div className="loading-message">Loading flight data...</div>;
    }

    return (
        <div className="booking-container">
            <h2 className="booking-header">{id === "-1" ? "Create New Booking" : "Confirm Your Flight"}</h2>
            
            <Formik
                initialValues={initialValues}
                enableReinitialize={true}
                onSubmit={onSubmit}
                validate={validate}
                validateOnBlur={true}
                validateOnChange={true}
            >
                {(props) => (
                    <form className="booking-form" onSubmit={props.handleSubmit}>
                        <div className="flight-info-card">
                            <h3 className="flight-info-header">Flight Information</h3>
                            
                            <div className="form-group">
                                <label className="form-label">Flight ID</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="flightId" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Departure From</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="departureFrom" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Arrival To</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="arrivalTo" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Departure Date</label>
                                <Field 
                                    type="date" 
                                    className="form-input"
                                    name="departure" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Departure Time</label>
                                <Field 
                                    type="time" 
                                    className="form-input"
                                    name="departureTime" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Return Date</label>
                                <Field 
                                    type="date" 
                                    className="form-input"
                                    name="returnDate" 
                                    readOnly
                                />
                            </div>
                            
                            <div className="form-group">
                                <label className="form-label">Seats Available</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="seatsAvailable" 
                                    readOnly
                                />
                            </div>
                        </div>

                        <div className="passenger-info-card">
                            <h3 className="passenger-info-header">Passenger Information</h3>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Number of Passengers</label>
                                <Field 
                                    type="number" 
                                    className="form-input"
                                    name="passenger" 
                                    placeholder="Enter passengers"
                                    min="1"
                                    max="10"
                                />
                                <ErrorMessage name="passenger" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 1 Full Name</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="passengerName" 
                                    placeholder="Enter passenger name as it appears on ID"
                                />
                                <ErrorMessage name="passengerName" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 1 Gender</label>
                                <Field 
                                    as="select"
                                    className="form-input"
                                    name="passengerAdult"
                                >
                                    <option value="">Select gender</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                    <option value="other">Other</option>
                                </Field>
                                <ErrorMessage name="passengerAdult" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 1 Date of Birth</label>
                                <Field 
                                    type="date" 
                                    className="form-input"
                                    name="dateOfBirth" 
                                />
                                <ErrorMessage name="dateOfBirth" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 2 Full Name</label>
                                <Field 
                                    type="text" 
                                    className="form-input"
                                    name="passengerName2" 
                                    placeholder="Enter passenger name as it appears on ID"
                                />
                                <ErrorMessage name="passengerName2" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 2 Gender</label>
                                <Field 
                                    as="select"
                                    className="form-input"
                                    name="passengerAdult2"
                                >
                                    <option value="">Select gender</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                    <option value="other">Other</option>
                                </Field>
                                <ErrorMessage name="passengerAdult2" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Passenger 2 Date of Birth</label>
                                <Field 
                                    type="date" 
                                    className="form-input"
                                    name="dateOfBirth2" 
                                />
                                <ErrorMessage name="dateOfBirth2" component="div" className="error-text" />
                            </div>
                            
                            <div className="form-group full-width">
                                <label className="form-label">Contact Number</label>
                                <Field 
                                    type="tel" 
                                    className="form-input"
                                    name="contactNo" 
                                    placeholder="Contact Number"
                                />
                                <ErrorMessage name="contactNo" component="div" className="error-text" />
                            </div>
                        </div>

                        <button className="submit-button" type="submit">
                            {id === "-1" ? "Create Booking" : "Confirm & Book Flight"}
                        </button>
                    </form>
                )}
            </Formik>
        </div>
    );
}
