import "./FlightList.css";
import { useAuth } from "../security/MainContext";

const authenticated = useAuth.authenticated;

function FlightList() {
  // State management
  const [state, setState] = useState({
    flights: [],
    message: null,
    searchParams: {
      departure: "",
      arrival: "",
      id: ""
    },
    loading: {
      main: false,
      search: false,
    }
  });

  // Chatbot state
