import { useEffect, useState,refreshFlights} from "react";
import { useNavigate } from 'react-router-dom';

import { AssistsAPI, DeleteFlightById, retrieveAFlightById, retrieveAllFlights, retrieveFlightFromTo } from "../api/CallingApi";

// import { useAuth } from "./security/AuthContext";

function FlightList(){ 

        // const authContext= useAuth()

        const [flights,setFlights] = useState([])

        const [message,setmessage] = useState(null)

        // const { departureFrom, arrivalTo } = response.data;

        const [departurePlace, setDeparturePlace] = useState("");
        const [arrivalPlace, setArrivalPlace] = useState("");

        //ai
        const [aiQuery, setAiQuery] = useState("");
        const [isAiLoading, setIsAiLoading] = useState(false);

        const[id,setid] = useState(" ")

        const nevigate=useNavigate();

        useEffect(() => {
           flightList();
     }, []);

        function flightList(){

                retrieveAllFlights()
                .then( (response) =>
                    {
                        // console.log(response.data)
                        setFlights(response.data)
                    } 
                )
                .catch( error =>console.log(error))
           
        }

        //ById
        function searchById() {  // Removed parameter since we'll use the id from state
        // if (!id.trim()) {
        //     setmessage("Please enter a flight ID");
        //     return;
        // }

        retrieveAFlightById(id)
            .then((response) => {
                if (response.data) {
                    // Wrap single flight in array for consistent rendering
                    setFlights([response.data]);
                    setmessage(null);
                } else {
                    setFlights([]);
                    setmessage("No flight found with this ID");
                }
            })
            .catch(error => {
                console.log(error);
                setmessage("Error searching for flight");
                setFlights([]);
            });
        }

 const searchByRoute = async (from, to) => {
  // Use provided params or fallback to state
  const departure = (from || searchParams.departure).trim();
  const arrival = (to || searchParams.arrival).trim();

  // Validate inputs
  if (!departure && !arrival) {
    setMessage("Please enter at least one location");
    return;
  }

  // Prepare API parameters (handle wildcards)
  const apiParams = {
    from: departure || "any",
    to: arrival || "any"
  };

  setMessage("Searching flights...");
  setFlights([]);
  setIsLoading(true);

  try {
    const response = await retrieveFlightFromTo(apiParams.from, apiParams.to);
    
    // Validate response structure
    if (!response?.data) {
      throw new Error("Invalid response format");
    }

    // Handle empty results
    if (response.data.length === 0) {
      setMessage(`No flights found from ${departure || 'anywhere'} to ${arrival || 'anywhere'}`);
    } else {
      setMessage(null);
    }

    setFlights(response.data);
  } catch (error) {
    console.error("Search failed:", error);
    
    let errorMessage = "Failed to search flights";
    if (error.response) {
      // Handle HTTP errors
      errorMessage = error.response.data?.message || 
                   `Server error: ${error.response.status}`;
    } else if (error.request) {
      // Handle network errors
      errorMessage = "Network error - please check your connection";
    }

    setMessage(errorMessage);
    setFlights([]);
  } finally {
    setIsLoading(false);
  }
};

        function HandleSearch(event){
                setid(event.target.value)
            }


        function DeleteFlight(id){
            //  console.log('delete operation' + id);

            DeleteFlightById(id)
            .then( 

                () => 
                    {
                    //display message
                    setmessage(`The flight with id ${id} is deleted`)
                    //update todolist
                    refreshFlights()

                }
                )
            .catch( error =>console.log(error))
        }

   function UpdateFlight(id){
    //  console.log('Update operation' + id);
     
     nevigate(`/AddNewFlight/${id}`)

   }

   function AddNewFlight(){

    // console.log('Create operation');
 
    nevigate(`/AddNewFlight/-1`)
   }

   //ai
   function handleAISearch() {
  if (!aiQuery.trim()) {
    setmessage("Please enter a search query");
    return;
  }

  setIsAiLoading(true);
  setmessage("AI is processing your request...");
  setFlights([]);

  AssistsAPI(aiQuery)
    .then(response => {
      if (!response?.data) {
        throw new Error("Invalid AI response");
      }

      const { departureFrom, arrivalTo } = response.data;
      
      // Case 1: Full route provided
      if (departureFrom && arrivalTo) {
        setDeparturePlace(departureFrom);
        setArrivalPlace(arrivalTo);
        searchByRoute(departureFrom, arrivalTo);
      }
      // Case 2: Only destination provided
      else if (arrivalTo) {
        setArrivalPlace(arrivalTo);
        retrieveFlightFromTo("any", arrivalTo) // Backend should handle "any" as wildcard
          .then((response) => {
            setFlights(response.data || []);
            setmessage(response.data?.length 
              ? `Showing flights to ${arrivalTo}` 
              : `No flights found to ${arrivalTo}`);
          });
      }
      // Case 3: Only origin provided
      else if (departureFrom) {
        setDeparturePlace(departureFrom);
        retrieveFlightFromTo(departureFrom, "any")
          .then((response) => {
            setFlights(response.data || []);
            setmessage(response.data?.length
              ? `Showing flights from ${departureFrom}`
              : `No flights found from ${departureFrom}`);
          });
      }
      else {
        setmessage("Please specify at least a destination (e.g. 'flights to Paris')");
      }
    })
    .catch(error => {
      console.error("AI search failed:", error);
      setmessage(error.response?.data?.message || 
                "AI service unavailable. Try manual search.");
    })
    .finally(() => setIsAiLoading(false));
}

    return(
        <div className="container">

           <h1>Flights Lists</h1>
           {message && <div className="alert alert-warning"> {message} </div> }

           <div className="row mt-3">
                <div className="col-md-5">
                    <input
                        type="text"
                        placeholder="Departure From"
                        value={departurePlace}
                        onChange={(e) => setDeparturePlace(e.target.value)}
                        className="form-control"
                    />
                </div>
                <div className="col-md-5">
                    <input
                        type="text"
                        placeholder="Arrival To"
                        value={arrivalPlace}
                        onChange={(e) => setArrivalPlace(e.target.value)}
                        className="form-control"
                    />
                </div>
                <div className="col-md-2">
                    <button 
  onClick={searchByRoute}
  className="btn btn-primary w-100"
  disabled={!departurePlace || !arrivalPlace || flights.length === 0}
>
  {message === "Searching flights..." ? (
    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
  ) : (
    "Search Route"
  )}
</button>
                </div>
           </div>
          <input 
                type="search" 
                placeholder="Enter flight ID" 
                value={id}
                onChange={HandleSearch} 
            />
            <button onClick={() => searchById()} >Search</button>
            <button className="m-2 btn btn-success" onClick={() => flightList()} >Show All</button>

            <div className="mb-4">
  <div className="input-group">
    <input
      type="text"
      className="form-control"
      placeholder="Ask naturally (e.g. 'to Mumbai' or 'from New York' or 'DEL to LHR')"
      value={aiQuery}
      onChange={(e) => setAiQuery(e.target.value)}
      onKeyPress={(e) => e.key === 'Enter' && handleAISearch()}
    />
    <button 
      className="btn btn-info"
      onClick={handleAISearch}
      disabled={isAiLoading}
    >
      {isAiLoading ? 'Searching...' : 'AI Search'}
    </button>
  </div>
</div>
           
           <table className='table'>

                <thead>
                    <tr>
                        <td>id</td>
                        <td>departureFrom</td>
                        <td>arrivalTo</td>
                        <td>departure</td>
                        <td>returnDate</td>
                        <td>seatsAvailable</td>
                    </tr>
                </thead>

                <tbody >
                    {
                        flights.map(
                            flight => (

                                <tr key={flight.id}>

                                    <td>{flight.id}</td>
                                    <td>{flight.departureFrom}</td>
                                    <td>{flight.arrivalTo}</td>
                                    <td>{flight.departure}</td>
                                    <td>{flight.returnDate}</td>
                                    <td>{flight.seatsAvailable}</td>
                        <td><button className="alert alert-warning"  onClick={ ()=> DeleteFlight(flight.id)}>Delete</button> </td>
                      <td><button className="alert alert-success"  onClick={ ()=> UpdateFlight(flight.id)}>Update</button> </td> 
                               </tr>
                            )
                        )
                        
                    }
                        <button className="btn btn-success m-5" onClick={AddNewFlight}> Add new Flight</button>
                     </tbody>
               

           </table>

        </div>
    )
}

export default FlightList
