// PaymentPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PaymentPage = () => {
    const { bookedId } = useParams();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchBookingDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/bookings/${bookedId}`);
                setBooking(response.data);
            } catch (err) {
                setError('Failed to fetch booking details');
                console.error('Error:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchBookingDetails();
    }, [bookedId]);

    const handlePayment = async (method) => {
        try {
            // Implement payment processing here
            const paymentData = {
                amount: booking.totalAmount,
                currency: 'INR',
                bookingId: parseInt(bookedId),
                flightId: booking.flightId,
                method: method
            };

            const response = await axios.post('http://localhost:8080/payment/booking', paymentData, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            alert('Payment successful!');
            // Handle successful payment
        } catch (err) {
            console.error('Payment error:', err);
            alert('Payment failed. Please try again.');
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;
    if (!booking) return <div>Booking not found</div>;

    return (
        <div className="payment-page">
            <h2>Complete Payment for Booking #{bookedId}</h2>
            
            <div className="booking-summary">
                <h3>Booking Details</h3>
                <p>Flight: {booking.flightNumber}</p>
                <p>Passenger: {booking.passengerName}</p>
                <p>Total Amount: â‚¹{booking.totalAmount}</p>
            </div>

            <div className="payment-options">
                <h3>Select Payment Method</h3>
                <button onClick={() => handlePayment('RAZORPAY')}>Pay with Razorpay</button>
                <button onClick={() => handlePayment('CARD')}>Credit/Debit Card</button>
                <button onClick={() => handlePayment('UPI')}>UPI</button>
            </div>
        </div>
    );
};

export default PaymentPage;
