import { useState } from 'react'
import {Link, useLocation, useNavigate} from 'react-router-dom'
import { useAuth } from '../security/MainContext'


export default function LoginComp(){

    const [username,setusername] = useState('')
    const [password,setPassword] = useState('')

    const [errorMessage,seterrorMessage] = useState(false)

    const nevigate=useNavigate();

    const authContext=useAuth();

    console.log(authContext);

   async function LoginHandle(){
        console.log('Login')
        const { state } = useLocation();

        //  if (state?.from) {
        //     nevigate(state.from); // Redirect back to where they came from
        // } else {
        //     nevigate('/'); // Default redirect
        // }

        if(await authContext.login(username,password)){
            nevigate(`/flights`)
        }else{
            console.log('Login Failed .Check your credentials. ')
            seterrorMessage(true)
        }
    }

    function HandleUsername(event){
        setusername(event.target.value)
    }

    function HandlePassword(event){
        setPassword(event.target.value)
    }

  return(
    <div className='LoginForm'>
        {/* {successMessage && <div className='successMessage' >Login Successfull</div> } */}
        {errorMessage && <div className='ErrorMessage' >Login Failed .Check your credentials.</div> }

        <div className="login ">
               <h1>Admin login page</h1> <hr />
            <div>
                <label className="m-2">Username</label>
                <input type="text" name='username' value={username} onChange={HandleUsername}/>
            </div>
            <div>
                <label className="m-2">PASSWORD</label>
                <input type="password" name='password' value={password} onChange={HandlePassword} />
            </div>
            <button className="btn btn-success m-3" onClick={LoginHandle}>Login</button>


        </div>

        <div className="mt-3 text-center">
                Don't have an account? <Link to="/register">Register here</Link>
            </div>
    </div>
  )
}
