import { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import { 
  AssistsAPI, 
  DeleteFlightById, 
  retrieveAFlightById, 
  retrieveAllFlights, 
  retrieveFlightFromTo 
} from "../api/CallingApi";

function FlightList() {
  const [flights, setFlights] = useState([]);
  const [message, setMessage] = useState(null);
  const [searchParams, setSearchParams] = useState({
    departure: "",
    arrival: "",
    id: ""
  });
  const [aiQuery, setAiQuery] = useState("");
  const [isAiLoading, setIsAiLoading] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  const navigate = useNavigate();

  // Fetch all flights on component mount
  useEffect(() => {
    fetchAllFlights();
  }, []);

  const fetchAllFlights = async () => {
    setIsLoading(true);
    try {
      const response = await retrieveAllFlights();
      setFlights(response.data);
      setMessage(null);
    } catch (error) {
      console.error("Failed to fetch flights:", error);
      setMessage("Failed to load flights. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSearchParamChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const searchById = async () => {
    if (!searchParams.id.trim()) {
      setMessage("Please enter a flight ID");
      return;
    }

    setIsLoading(true);
    try {
      const response = await retrieveAFlightById(searchParams.id);
      if (response.data) {
        setFlights([response.data]);
        setMessage(null);
      } else {
        setFlights([]);
        setMessage("No flight found with this ID");
      }
    } catch (error) {
      console.error("Search error:", error);
      setMessage("Error searching for flight");
      setFlights([]);
    } finally {
      setIsLoading(false);
    }
  };

  const searchByRoute = async (from, to) => {
    const departure = from || searchParams.departure;
    const arrival = to || searchParams.arrival;
    const effectiveFrom = departure.trim() || "any";
    const effectiveTo = arrival.trim() || "any";

    setIsLoading(true);
    setMessage("Searching flights...");
    
    try {
      const response = await retrieveFlightFromTo(effectiveFrom, effectiveTo);
      setFlights(response.data || []);
      setMessage(response.data?.length ? null : "No flights found for this route");
    } catch (error) {
      console.error("Route search error:", error);
      setMessage(error.response?.data?.message || "Failed to search flights");
      setFlights([]);
    } finally {
      setIsLoading(false);
    }
  };

  const deleteFlight = async (id) => {
    if (!window.confirm(`Are you sure you want to delete flight ${id}?`)) return;
    
    try {
      await DeleteFlightById(id);
      setMessage(`Flight ${id} deleted successfully`);
      await fetchAllFlights(); // Refresh the list
    } catch (error) {
      console.error("Delete error:", error);
      setMessage("Failed to delete flight");
    }
  };

  const handleAISearch = async () => {
    if (!aiQuery.trim()) {
      setMessage("Please enter a search query");
      return;
    }

    setIsAiLoading(true);
    setMessage("AI is processing your request...");
    setFlights([]);

    try {
      const response = await AssistsAPI(aiQuery);
      if (!response?.data) throw new Error("Invalid AI response");

      const { departureFrom, arrivalTo } = response.data;
      
      if (departureFrom && arrivalTo) {
        // Update search fields and perform search
        setSearchParams(prev => ({
          ...prev,
          departure: departureFrom,
          arrival: arrivalTo
        }));
        await searchByRoute(departureFrom, arrivalTo);
      } 
      else if (arrivalTo) {
        setSearchParams(prev => ({ ...prev, arrival: arrivalTo }));
        const response = await retrieveFlightFromTo("any", arrivalTo);
        setFlights(response.data || []);
        setMessage(response.data?.length 
          ? `Showing flights to ${arrivalTo}` 
          : `No flights found to ${arrivalTo}`);
      }
      else if (departureFrom) {
        setSearchParams(prev => ({ ...prev, departure: departureFrom }));
        const response = await retrieveFlightFromTo(departureFrom, "any");
        setFlights(response.data || []);
        setMessage(response.data?.length
          ? `Showing flights from ${departureFrom}`
          : `No flights found from ${departureFrom}`);
      }
      else {
        setMessage("Please specify at least a destination (e.g. 'flights to Paris')");
      }
    } catch (error) {
      console.error("AI search failed:", error);
      setMessage(error.response?.data?.message || "AI service unavailable. Try manual search.");
    } finally {
      setIsAiLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Flight List</h1>
      
      {message && (
        <div className={`alert ${message.includes("success") ? "alert-success" : "alert-warning"}`}>
          {message}
        </div>
      )}

      {/* Search Controls */}
      <div className="card mb-4">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-5">
              <input
                type="text"
                name="departure"
                placeholder="Departure From"
                className="form-control"
                value={searchParams.departure}
                onChange={handleSearchParamChange}
              />
            </div>
            <div className="col-md-5">
              <input
                type="text"
                name="arrival"
                placeholder="Arrival To"
                className="form-control"
                value={searchParams.arrival}
                onChange={handleSearchParamChange}
              />
            </div>
            <div className="col-md-2">
              <button
                className="btn btn-primary w-100"
                onClick={() => searchByRoute()}
                disabled={isLoading}
              >
                {isLoading ? "Searching..." : "Search Route"}
              </button>
            </div>
          </div>

          <div className="row mt-3 g-3 align-items-center">
            <div className="col-md-4">
              <div className="input-group">
                <input
                  type="search"
                  name="id"
                  placeholder="Flight ID"
                  className="form-control"
                  value={searchParams.id}
                  onChange={handleSearchParamChange}
                />
                <button
                  className="btn btn-outline-secondary"
                  onClick={searchById}
                  disabled={isLoading}
                >
                  Search
                </button>
              </div>
            </div>
            <div className="col-md-2">
              <button
                className="btn btn-success w-100"
                onClick={fetchAllFlights}
                disabled={isLoading}
              >
                Show All
              </button>
            </div>
          </div>

          <div className="mt-3">
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Ask naturally (e.g. 'flights to Paris')"
                value={aiQuery}
                onChange={(e) => setAiQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAISearch()}
              />
              <button
                className="btn btn-info"
                onClick={handleAISearch}
                disabled={isAiLoading || isLoading}
              >
                {isAiLoading ? 'Processing...' : 'AI Search'}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Flight Table */}
      <div className="table-responsive">
        <table className="table table-striped table-hover">
          <thead className="table-dark">
            <tr>
              <th>ID</th>
              <th>From</th>
              <th>To</th>
              <th>Departure</th>
              <th>Return</th>
              <th>Seats</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {flights.length > 0 ? (
              flights.map(flight => (
                <tr key={flight.id}>
                  <td>{flight.id}</td>
                  <td>{flight.departureFrom}</td>
                  <td>{flight.arrivalTo}</td>
                  <td>{new Date(flight.departure).toLocaleString()}</td>
                  <td>{flight.returnDate ? new Date(flight.returnDate).toLocaleString() : 'N/A'}</td>
                  <td>{flight.seatsAvailable}</td>
                  <td>
                    <div className="btn-group">
                      <button
                        className="btn btn-sm btn-warning"
                        onClick={() => navigate(`/AddNewFlight/${flight.id}`)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => deleteFlight(flight.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center">
                  {isLoading ? 'Loading flights...' : 'No flights found'}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      <button
        className="btn btn-success mt-3"
        onClick={() => navigate('/AddNewFlight/-1')}
      >
        Add New Flight
      </button>
    </div>
  );
}

export default FlightList;
