import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { PaymentAPI } from "../api/CallingApi";
import "./PaymentPage.css";

export default function PaymentPage() {
    const navigate = useNavigate();
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState("credit");
    
    // Get booking details from navigation state
    const { bookingDetails, passengerCount } = location.state || {};
    const [paymentAmount] = useState(passengerCount * 5000); // Example: 5000 per passenger

    useEffect(() => {
        if (!bookingDetails) {
            navigate("/"); // Redirect if no booking details
        }
    }, [bookingDetails, navigate]);

    const handlePayment = async () => {
        try {
            setLoading(true);
            setError(null);
            
            const paymentData = {
                flightBookedId: bookingDetails.id,
                userId: 1, // You should get this from auth context or localStorage
                paymentAmount: paymentAmount
            };

            const response = await PaymentAPI(paymentData);
            
            // Redirect to success page after successful payment
            navigate("/booking-success", {
                state: {
                    bookingId: bookingDetails.id,
                    paymentId: response.data.id
                }
            });
            
        } catch (error) {
            console.error("Payment failed:", error);
            setError("Payment failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    if (!bookingDetails) {
        return <div>No booking details found. Please start over.</div>;
    }

    return (
        <div className="payment-container">
            <h2>Complete Your Payment</h2>
            
            <div className="payment-summary">
                <h3>Booking Summary</h3>
                <p>Flight: {bookingDetails.flight.departureFrom} to {bookingDetails.flight.arrivalTo}</p>
                <p>Passengers: {passengerCount}</p>
                <p>Total Amount: ₹{paymentAmount}</p>
            </div>
            
            <div className="payment-methods">
                <h3>Select Payment Method</h3>
                
                <div className="method-options">
                    <label>
                        <input
                            type="radio"
                            value="credit"
                            checked={paymentMethod === "credit"}
                            onChange={() => setPaymentMethod("credit")}
                        />
                        Credit Card
                    </label>
                    
                    <label>
                        <input
                            type="radio"
                            value="debit"
                            checked={paymentMethod === "debit"}
                            onChange={() => setPaymentMethod("debit")}
                        />
                        Debit Card
                    </label>
                    
                    <label>
                        <input
                            type="radio"
                            value="upi"
                            checked={paymentMethod === "upi"}
                            onChange={() => setPaymentMethod("upi")}
                        />
                        UPI
                    </label>
                    
                    <label>
                        <input
                            type="radio"
                            value="netbanking"
                            checked={paymentMethod === "netbanking"}
                            onChange={() => setPaymentMethod("netbanking")}
                        />
                        Net Banking
                    </label>
                </div>
                
                {paymentMethod === "credit" || paymentMethod === "debit" ? (
                    <div className="card-details">
                        <input type="text" placeholder="Card Number" />
                        <input type="text" placeholder="Card Holder Name" />
                        <div className="card-row">
                            <input type="text" placeholder="MM/YY" />
                            <input type="text" placeholder="CVV" />
                        </div>
                    </div>
                ) : paymentMethod === "upi" ? (
                    <div className="upi-details">
                        <input type="text" placeholder="UPI ID" />
                    </div>
                ) : (
                    <div className="netbanking-details">
                        <select>
                            <option value="">Select Bank</option>
                            <option value="sbi">State Bank of India</option>
                            <option value="hdfc">HDFC Bank</option>
                            <option value="icici">ICICI Bank</option>
                        </select>
                    </div>
                )}
            </div>
            
            {error && <div className="error-message">{error}</div>}
            
            <button 
                className="pay-now-button"
                onClick={handlePayment}
                disabled={loading}
            >
                {loading ? "Processing..." : "Pay Now"}
            </button>
        </div>
    );
}
