import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types'; // Add this import

const RazorpayPayment = ({ orderDetails }) => {
  // Safe destructuring with defaults
  const { 
    orderId = 'order_R7gN7kL62tA4fn', 
    amount = 900000, 
    currency = 'INR' 
  } = orderDetails || {};

  const handlePayment = async () => {
    if (!orderId) {
      alert('Order information is missing');
      return;
    }

    try {
      const script = document.createElement('script');
      script.src = "https://checkout.razorpay.com/v1/checkout.js";
      script.async = true;
      document.body.appendChild(script);

      script.onload = () => {
        const options = {
          key: "rzp_test_l5MXTWsTyrUqwN",
          amount: amount,
          currency: currency,
          order_id: orderId,
          name: "Flight Booking",
          description: "Payment for your booking",
          handler: async function(response) {
            try {
              await axios.post('http://localhost:8080/paymentconfirm-booking', {
                orderId: response.razorpay_order_id,
                paymentId: response.razorpay_payment_id,
                signature: response.razorpay_signature
              });
              alert('Payment successful! Booking confirmed.');
            } catch (error) {
              console.error('Confirmation failed:', error);
              alert('Payment succeeded but confirmation failed. Please contact support.');
            }
          },
          prefill: {
            name: "Passenger Name",
            email: "passenger@example.com",
            contact: "9876543210"
          },
          theme: {
            color: "#3399cc"
          }
        };

        const rzp = new window.Razorpay(options);
        rzp.on('payment.failed', (response) => {
          alert(`Payment failed: ${response.error.description}`);
        });
        rzp.open();
      };
    } catch (error) {
      console.error('Payment initialization failed:', error);
      alert('Failed to initialize payment');
    }
  };

  return (
    <div className="payment-container">
      <h3>Complete Payment</h3>
      <p>Order ID: {orderId}</p>
      <p>Amount: â‚¹{(amount / 100).toLocaleString('en-IN')}</p>
      <button onClick={handlePayment} className="pay-button">
        Pay Now
      </button>
    </div>
  );
};

// Prop type validation
RazorpayPayment.propTypes = {
  orderDetails: PropTypes.shape({
    orderId: PropTypes.string.isRequired,
    amount: PropTypes.number.isRequired,
    currency: PropTypes.string
  }).isRequired
};

export default RazorpayPayment;
