import React, { useState, useEffect, useRef, useContext } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';
import { useAuth } from './MainContext';
import './SeatSelection.css';

const SeatSelectionPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { flightId } = useParams();
    const authContext = useAuth();
    
    const [bookingDetails, setBookingDetails] = useState(null);
    const [passengerCount, setPassengerCount] = useState(1);
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [unavailableSeats, setUnavailableSeats] = useState(new Set());
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [connected, setConnected] = useState(false);
    
    const stompClient = useRef(null);
    const seatPrice = 500; // Additional charge for seat selection
    const userId = authContext.userId; // Get userId from authentication context

    // Seat configuration
    const rows = 10;
    const seatsPerRow = 6;

    useEffect(() => {
        // Get booking details from location state or fetch from API
        if (location.state) {
            setBookingDetails(location.state.bookingDetails);
            setPassengerCount(location.state.passengerCount || 1);
        } else if (flightId) {
            // If no state passed, fetch flight details using flightId from URL
            fetchFlightDetails(flightId);
        } else {
            navigate('/flights');
            return;
        }

        // Initialize WebSocket connection
        initializeWebSocket();

        return () => {
            // Clean up WebSocket connection on component unmount
            if (stompClient.current) {
                stompClient.current.deactivate();
            }
        };
    }, [flightId, location.state]);

    const fetchFlightDetails = async (flightId) => {
        try {
            setLoading(true);
            // You would need to implement this API call based on your existing endpoints
            // const response = await retrieveAFlightById(flightId);
            // setBookingDetails(response.data);
        } catch (err) {
            setError('Failed to load flight details');
            navigate('/flights');
        } finally {
            setLoading(false);
        }
    };

    const initializeWebSocket = () => {
        const socket = new SockJS('http://localhost:8080/ws');
        stompClient.current = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);
                console.log('Connected to WebSocket');
                
                // Subscribe to seat updates for this flight
                stompClient.current.subscribe(
                    `/topic/flight/${flightId || bookingDetails?.flightId}/seats`, 
                    (message) => {
                        const updatedUnavailableSeats = JSON.parse(message.body);
                        setUnavailableSeats(new Set(updatedUnavailableSeats));
                    }
                );
                
                // Request initial unavailable seats
                stompClient.current.publish({
                    destination: `/app/flight/${flightId || bookingDetails?.flightId}/seats`,
                    body: JSON.stringify({})
                });
            },
            onStompError: (error) => {
                console.error('WebSocket error:', error);
                setError('Connection error. Seat availability may not be up to date.');
            }
        });
        
        stompClient.current.activate();
    };

    const handleSeatSelect = (seatNumber) => {
        if (unavailableSeats.has(seatNumber)) return;

        setSelectedSeats(prev => {
            if (prev.includes(seatNumber)) {
                releaseSeat(seatNumber);
                return prev.filter(seat => seat !== seatNumber);
            } else if (prev.length < passengerCount) {
                reserveSeat(seatNumber);
                return [...prev, seatNumber];
            }
            return prev;
        });
    };

    const reserveSeat = (seatNumber) => {
        if (stompClient.current && connected) {
            const reservationRequest = {
                flightId: flightId || bookingDetails?.flightId,
                seatNumber: seatNumber,
                userId: userId
            };
            
            stompClient.current.publish({
                destination: '/app/reserve-seat',
                body: JSON.stringify(reservationRequest)
            });
        }
    };

    const releaseSeat = (seatNumber) => {
        if (stompClient.current && connected) {
            const reservationRequest = {
                flightId: flightId || bookingDetails?.flightId,
                seatNumber: seatNumber,
                userId: userId
            };
            
            stompClient.current.publish({
                destination: '/app/release-seat',
                body: JSON.stringify(reservationRequest)
            });
        }
    };

    const handleConfirmSeats = async () => {
        if (selectedSeats.length !== passengerCount) {
            setError(`Please select exactly ${passengerCount} seat(s)`);
            return;
        }

        try {
            setLoading(true);
            
            // Create booking with selected seats
            const bookingData = {
                flightId: flightId || bookingDetails?.flightId,
                userId: userId,
                passengers: passengerCount,
                selectedSeats: selectedSeats,
                totalAmount: (bookingDetails?.totalAmount || 0) + (selectedSeats.length * seatPrice)
            };
            
            // Use your existing BookedFlightAPI
            const response = await BookedFlightAPI(bookingData);
            
            if (response.data && response.data.bookingId) {
                // Navigate to payment page with booking ID
                navigate(`/flights/booking/${response.data.bookingId}`, {
                    state: {
                        bookingDetails: response.data,
                        selectedSeats,
                        seatCharges: selectedSeats.length * seatPrice,
                        totalAmount: response.data.totalAmount
                    }
                });
            } else {
                setError('Failed to create booking');
            }
        } catch (err) {
            setError('Failed to save seat selection');
            console.error('Booking error:', err);
        } finally {
            setLoading(false);
        }
    };

    const renderSeatMap = () => {
        const seatMap = [];
        
        // Add cockpit area
        seatMap.push(
            <div key="cockpit" className="cockpit-area">
                <div className="cockpit">Cockpit</div>
            </div>
        );

        // Add seat rows
        for (let row = 1; row <= rows; row++) {
            const rowSeats = [];
            
            // Left side seats (A, B, C)
            for (let seat = 1; seat <= 3; seat++) {
                const seatNumber = (row - 1) * seatsPerRow + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isUnavailable = unavailableSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                const isEmergencyExit = row === 1 || row === rows;
                
                rowSeats.push(
                    <div
                        key={`left-${seatNumber}`}
                        className={`seat ${isUnavailable ? 'booked' : ''} ${isSelected ? 'selected' : ''} ${isEmergencyExit ? 'emergency' : ''}`}
                        onClick={() => !isUnavailable && handleSeatSelect(seatNumber)}
                    >
                        {seatLabel}
                    </div>
                );
            }

            // Aisle
            rowSeats.push(<div key={`aisle-${row}`} className="aisle"></div>);

            // Right side seats (D, E, F)
            for (let seat = 4; seat <= 6; seat++) {
                const seatNumber = (row - 1) * seatsPerRow + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isUnavailable = unavailableSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                const isEmergencyExit = row === 1 || row === rows;
                
                rowSeats.push(
                    <div
                        key={`right-${seatNumber}`}
                        className={`seat ${isUnavailable ? 'booked' : ''} ${isSelected ? 'selected' : ''} ${isEmergencyExit ? 'emergency' : ''}`}
                        onClick={() => !isUnavailable && handleSeatSelect(seatNumber)}
                    >
                        {seatLabel}
                    </div>
                );
            }

            seatMap.push(
                <div key={`row-${row}`} className="seat-row">
                    {rowSeats}
                </div>
            );
        }

        return seatMap;
    };

    if (!bookingDetails && !flightId) {
        return <div>No booking information found. Please start over.</div>;
    }

    return (
        <div className="seat-selection-page">
            <div className="container">
                <div className="header">
                    <h1>Select Your Seats</h1>
                    {bookingDetails && (
                        <p>Flight {bookingDetails.flightNumber} - {bookingDetails.departureFrom} to {bookingDetails.arrivalTo}</p>
                    )}
                    <div className={`connection-status ${connected ? 'connected' : 'disconnected'}`}>
                        {connected ? 'Live Updates Enabled' : 'Connecting...'}
                    </div>
                </div>

                {bookingDetails && (
                    <div className="booking-summary">
                        <h3>Booking Summary</h3>
                        <div className="summary-details">
                            <p><strong>Booking Reference:</strong> {bookingDetails.bookingReference}</p>
                            <p><strong>Passengers:</strong> {passengerCount}</p>
                            <p><strong>Flight Date:</strong> {bookingDetails.departureDate}</p>
                            <p><strong>Departure Time:</strong> {bookingDetails.departureTime}</p>
                        </div>
                    </div>
                )}

                {error && <div className="error-message">{error}</div>}

                <div className="seat-selection-container">
                    <div className="airplane-layout">
                        <div className="airplane">
                            {renderSeatMap()}
                        </div>
                    </div>

                    <div className="selection-info">
                        <div className="seat-legend">
                            <h4>Seat Legend</h4>
                            <div className="legend-item">
                                <div className="seat available"></div>
                                <span>Available</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat selected"></div>
                                <span>Selected (Temporarily Reserved)</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat booked"></div>
                                <span>Unavailable</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat emergency"></div>
                                <span>Emergency Exit</span>
                            </div>
                        </div>

                        <div className="selected-seats-info">
                            <h4>Your Selection</h4>
                            {selectedSeats.length > 0 ? (
                                <div>
                                    <p>Selected Seats: {selectedSeats.map(seat => {
                                        const row = Math.ceil(seat / seatsPerRow);
                                        const seatLetter = String.fromCharCode(64 + (seat % seatsPerRow === 0 ? seatsPerRow : seat % seatsPerRow));
                                        return `${row}${seatLetter}`;
                                    }).join(', ')}</p>
                                    <p>Seat Charges: ₹{selectedSeats.length * seatPrice}</p>
                                    {bookingDetails && (
                                        <p><strong>Total Amount: ₹{bookingDetails.totalAmount + (selectedSeats.length * seatPrice)}</strong></p>
                                    )}
                                </div>
                            ) : (
                                <p>No seats selected yet</p>
                            )}
                        </div>

                        <div className="action-buttons">
                            <button 
                                className="btn-back"
                                onClick={() => navigate(-1)}
                                disabled={loading}
                            >
                                Back to Booking
                            </button>
                            <button 
                                className="btn-confirm"
                                onClick={handleConfirmSeats}
                                disabled={loading || selectedSeats.length !== passengerCount}
                            >
                                {loading ? 'Processing...' : `Confirm ${selectedSeats.length}/${passengerCount} Seats`}
                            </button>
                        </div>
                    </div>
                </div>

                <div className="seat-info-note">
                    <p><strong>Note:</strong> Seat selection charges are ₹{seatPrice} per seat. Emergency exit rows have extra legroom.</p>
                    <p>Selected seats are temporarily reserved for you while you complete your booking.</p>
                </div>
            </div>
        </div>
    );
};

export default SeatSelectionPage;
