import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelection.css';

const SeatSelectionPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { bookingDetails, passengerCount } = location.state || {};
    
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [bookedSeats, setBookedSeats] = useState(new Set());
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // Sample seat map data - in real app, this would come from API
    const rows = 10;
    const seatsPerRow = 6;
    const seatPrice = 500; // Additional charge for seat selection

    useEffect(() => {
        if (!bookingDetails) {
            navigate('/flights');
            return;
        }

        // Fetch already booked seats from API
        fetchBookedSeats();
    }, [bookingDetails]);

    const fetchBookedSeats = async () => {
        try {
            setLoading(true);
            // Simulate API call to get booked seats
            // const response = await axios.get(`/api/flights/${bookingDetails.flightId}/seats`);
            // setBookedSeats(new Set(response.data.bookedSeats));
            
            // For demo, set some random booked seats
            const demoBooked = new Set([12, 15, 23, 34, 45, 56]);
            setBookedSeats(demoBooked);
        } catch (err) {
            setError('Failed to load seat information');
        } finally {
            setLoading(false);
        }
    };

    const handleSeatSelect = (seatNumber) => {
        if (bookedSeats.has(seatNumber)) return; // Can't select already booked seat

        setSelectedSeats(prev => {
            if (prev.includes(seatNumber)) {
                // Deselect seat
                return prev.filter(seat => seat !== seatNumber);
            } else if (prev.length < passengerCount) {
                // Select seat if we haven't reached passenger count
                return [...prev, seatNumber];
            }
            return prev; // Can't select more than passenger count
        });
    };

    const handleConfirmSeats = async () => {
        if (selectedSeats.length !== passengerCount) {
            setError(`Please select exactly ${passengerCount} seat(s)`);
            return;
        }

        try {
            setLoading(true);
            // Save selected seats to booking
            // await axios.post(`/api/bookings/${bookingDetails.id}/seats`, { seats: selectedSeats });
            
            // Navigate to payment page
            navigate('/payment', {
                state: {
                    bookingDetails,
                    selectedSeats,
                    seatCharges: selectedSeats.length * seatPrice,
                    totalAmount: bookingDetails.totalAmount + (selectedSeats.length * seatPrice)
                }
            });
        } catch (err) {
            setError('Failed to save seat selection');
        } finally {
            setLoading(false);
        }
    };

    const renderSeatMap = () => {
        const seatMap = [];
        
        // Add cockpit area
        seatMap.push(
            <div key="cockpit" className="cockpit-area">
                <div className="cockpit">Cockpit</div>
            </div>
        );

        // Add seat rows
        for (let row = 1; row <= rows; row++) {
            const rowSeats = [];
            
            // Left side seats (A, B, C)
            for (let seat = 1; seat <= 3; seat++) {
                const seatNumber = row * 10 + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isBooked = bookedSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                
                rowSeats.push(
                    <div
                        key={`left-${seatNumber}`}
                        className={`seat ${isBooked ? 'booked' : ''} ${isSelected ? 'selected' : ''}`}
                        onClick={() => !isBooked && handleSeatSelect(seatNumber)}
                    >
                        {seatLabel}
                    </div>
                );
            }

            // Aisle
            rowSeats.push(<div key={`aisle-${row}`} className="aisle"></div>);

            // Right side seats (D, E, F)
            for (let seat = 4; seat <= 6; seat++) {
                const seatNumber = row * 10 + seat;
                const seatLabel = `${row}${String.fromCharCode(64 + seat)}`;
                const isBooked = bookedSeats.has(seatNumber);
                const isSelected = selectedSeats.includes(seatNumber);
                
                rowSeats.push(
                    <div
                        key={`right-${seatNumber}`}
                        className={`seat ${isBooked ? 'booked' : ''} ${isSelected ? 'selected' : ''}`}
                        onClick={() => !isBooked && handleSeatSelect(seatNumber)}
                    >
                        {seatLabel}
                    </div>
                );
            }

            seatMap.push(
                <div key={`row-${row}`} className="seat-row">
                    {rowSeats}
                </div>
            );
        }

        return seatMap;
    };

    if (!bookingDetails) {
        return <div>No booking information found. Please start over.</div>;
    }

    return (
        <div className="seat-selection-page">
            <div className="container">
                <div className="header">
                    <h1>Select Your Seats</h1>
                    <p>Flight {bookingDetails.flightNumber} - {bookingDetails.departureFrom} to {bookingDetails.arrivalTo}</p>
                </div>

                <div className="booking-summary">
                    <h3>Booking Summary</h3>
                    <div className="summary-details">
                        <p><strong>Booking Reference:</strong> {bookingDetails.bookingReference}</p>
                        <p><strong>Passengers:</strong> {passengerCount}</p>
                        <p><strong>Flight Date:</strong> {bookingDetails.departureDate}</p>
                        <p><strong>Departure Time:</strong> {bookingDetails.departureTime}</p>
                    </div>
                </div>

                {error && <div className="error-message">{error}</div>}

                <div className="seat-selection-container">
                    <div className="airplane-layout">
                        <div className="airplane">
                            {renderSeatMap()}
                        </div>
                    </div>

                    <div className="selection-info">
                        <div className="seat-legend">
                            <h4>Seat Legend</h4>
                            <div className="legend-item">
                                <div className="seat available"></div>
                                <span>Available</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat selected"></div>
                                <span>Selected</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat booked"></div>
                                <span>Booked</span>
                            </div>
                            <div className="legend-item">
                                <div className="seat emergency"></div>
                                <span>Emergency Exit</span>
                            </div>
                        </div>

                        <div className="selected-seats-info">
                            <h4>Your Selection</h4>
                            {selectedSeats.length > 0 ? (
                                <div>
                                    <p>Selected Seats: {selectedSeats.map(seat => 
                                        `${Math.floor(seat/10)}${String.fromCharCode(64 + (seat % 10))}`
                                    ).join(', ')}</p>
                                    <p>Seat Charges: ₹{selectedSeats.length * seatPrice}</p>
                                    <p><strong>Total Amount: ₹{bookingDetails.totalAmount + (selectedSeats.length * seatPrice)}</strong></p>
                                </div>
                            ) : (
                                <p>No seats selected yet</p>
                            )}
                        </div>

                        <div className="action-buttons">
                            <button 
                                className="btn-back"
                                onClick={() => navigate(-1)}
                                disabled={loading}
                            >
                                Back to Booking
                            </button>
                            <button 
                                className="btn-confirm"
                                onClick={handleConfirmSeats}
                                disabled={loading || selectedSeats.length !== passengerCount}
                            >
                                {loading ? 'Processing...' : `Confirm ${selectedSeats.length}/${passengerCount} Seats`}
                            </button>
                        </div>
                    </div>
                </div>

                <div className="seat-info-note">
                    <p><strong>Note:</strong> Seat selection charges are ₹{seatPrice} per seat. Emergency exit rows have extra legroom.</p>
                </div>
            </div>
        </div>
    );
};

export default SeatSelectionPage;
